import React from "react";
import { Col, Row, Container } from "../../components/Grid";
import { Input, TextArea, FormBtn } from "../../components/Form";
import API from "../../utils/API";
import Nav from "../../components/Nav/"



class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstName: "",
      lastName: "",
      email:"",
      message: ""
    };
  }

  handleInputChange = event => {
    // Getting the value and name of the input which triggered the change
    const { name, value } = event.target;

    // Updating the input's state
    this.setState({
      [name]: value
    });
  };

  handleFormSubmit = event => {
    // Preventing the default behavior of the form submit (which is to refresh the page)
    event.preventDefault();
    if(this.state.firstName && this.state.lastName && this.state.email){
      API.saveLead({
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        email: this.state.email,
        message: this.state.message
      })
        .then(res => res.data)
        .catch(err => console.log(err));
    }
    // Alert the user their first and last name, clear `this.state.firstName` and `this.state.lastName`, clearing the inputs
    alert(`Hello ${this.state.firstName} ${this.state.lastName}, thank you for your message. I will reach out to you within 24hrs`);
    this.setState({
      firstName: "",
      lastName: "",
      email:"",
      message:""
      
    });


  };

  render() {
    return (
      <Container style={{ background: "orange" }} fluid >
        <Nav/>
        <Row>
          <Col size="md-12  sm-12">
          <h1>Large Image of Networking in Action</h1>
          <div><i className="devicon-react-original-wordmark"></i></div>

          </Col>
        </Row>
        <Row>
        <Col size="md-4  sm-12">
        </Col>
          <Col size="md-4  sm-12">
          <h1>Let's Work Together...</h1>


            <Input value={this.state.firstName} onChange={this.handleInputChange}
            name="firstName"
            placeholder="First Name"/>
            
            <Input value={this.state.lastName} onChange={this.handleInputChange}
            name="lastName"
            placeholder="Last Name"/>

            <Input value={this.state.email} onChange={this.handleInputChange}
            name="email"
            placeholder="e-mail"/>

            <TextArea style={{height: "200px"}} value={this.state.message} onChange={this.handleInputChange}
            name="message"
            placeholder="How can I help you"/>

            <FormBtn disabled={!(this.state.firstName && this.state.lastName && this.state.email)} onClick={this.handleFormSubmit}>Submit</FormBtn>
          </Col>
          <Col size="md-4">
          </Col>
          <Col size="md-12  sm-12" >
          <p style={{ marginLeft:"40px",marginRight:"40px" }}>Lorem ipsum dolor sit amet, consectetur adipiscing elit. Vivamus feugiat odio dui, vel scelerisque lorem faucibus sit amet. Fusce sem dolor, dignissim a mattis eget, cursus sit amet purus. Nullam tortor tortor, eleifend nec hendrerit consectetur, ultrices sit amet nibh. Cras non nibh vestibulum, dictum mi vitae, tempus purus. Morbi bibendum eget libero quis semper. Duis vehicula quam dolor, sit amet fringilla felis cursus a. Suspendisse felis lacus, tincidunt ac tincidunt id, fringilla eget turpis. Proin sit amet dolor sed sapien condimentum volutpat et et dui. Donec maximus mi a velit faucibus, ut feugiat odio imperdiet. Phasellus et viverra nunc. Nulla ac dictum tellus. Fusce a augue in mi efficitur convallis. Pellentesque placerat pellentesque dui eget dapibus. Curabitur eu eleifend augue.</p>
          <div>
            <h1>
            social media links here
            </h1>
            </div>
          </Col>
          
        </Row>
      </Container>
    )
  }
}
export default Contact;
